import React from "react";

export function TreeConnectors({ treeData, nodePositions, containerRect }) {
  // Only draw lines for nodes that have a parent (parent !== 0)
  const lines = [];
  for (const node of treeData) {
    if (node.parent === 0) continue;
    const childPos = nodePositions[node.id];
    const parentPos = nodePositions[node.parent];
    if (!childPos || !parentPos) continue;
    // Draw from parent's right edge to child's left edge, vertically centered
    lines.push(
      <line
        key={`${node.parent}-${node.id}`}
        x1={parentPos.right - containerRect.left}
        y1={parentPos.top + parentPos.height / 2 - containerRect.top}
        x2={childPos.left - containerRect.left}
        y2={childPos.top + childPos.height / 2 - containerRect.top}
        stroke="#dfdfe2"
        strokeWidth={2}
        fill="none"
      />
    );
  }
  return (
    <svg
      style={{
        position: "absolute",
        top: 0,
        left: 0,
        width: containerRect.width,
        height: containerRect.height,
        pointerEvents: "none",
        zIndex: 0,
      }}
      width={containerRect.width}
      height={containerRect.height}
    >
      {lines}
    </svg>
  );
}
