import React, { useState, useRef, useEffect } from 'react';
import styles from './NodeCard.module.css';

export default function NodeCard({ text, onAdd, hasConnection, childrenNodes }) {
  const [isEditing, setIsEditing] = useState(text === '');
  const [nodeText, setNodeText] = useState(text);
  const inputRef = useRef(null);

  useEffect(() => {
    if (isEditing && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isEditing]);

  const handleTextChange = (e) => setNodeText(e.target.value);
  const handleBlur = () => setIsEditing(false);
  const handleKeyDown = (e) => {
    if (e.key === 'Enter') setIsEditing(false);
  };

  return (
    <div className={styles.nodeCardContainer}>
      {hasConnection && <div className={styles.connectionDot} />}
      <div className={styles.nodeCard}>
        {isEditing ? (
          <input
            ref={inputRef}
            className={styles.nodeInput}
            value={nodeText}
            onChange={handleTextChange}
            onBlur={handleBlur}
            onKeyDown={handleKeyDown}
            placeholder="Enter name"
          />
        ) : (
          <span className={styles.nodeText} onClick={() => setIsEditing(true)}>{nodeText || 'Unnamed'}</span>
        )}
        <button className={styles.addNodeButton} onClick={onAdd} title="Add node">
          <svg width="12" height="12" viewBox="0 0 12 12" fill="none" xmlns="http://www.w3.org/2000/svg">
            <circle cx="6" cy="6" r="5.4" stroke="#121214" strokeWidth="1.2" />
            <rect x="5" y="2.5" width="2" height="7" rx="1" fill="#121214" />
            <rect x="2.5" y="5" width="7" height="2" rx="1" fill="#121214" />
          </svg>
        </button>
      </div>
      {childrenNodes}
    </div>
  );
}
