import React, { useState } from 'react';
import NodeCard from './NodeCard';
import styles from './HierarchyTreePage.module.css';

const initialNodes = [
  { id: 1, text: 'West coast', children: [] }
];

export default function HierarchyTreePage() {
  const [nodes, setNodes] = useState(initialNodes);

  const handleAddNode = (parentId) => {
    setNodes(prevNodes => {
      const newId = prevNodes.length + 1;
      return prevNodes.map(node => {
        if (node.id === parentId) {
          return {
            ...node,
            children: [...node.children, { id: newId, text: '', children: [] }]
          };
        }
        return node;
      });
    });
  };

  const renderNodes = (nodes, parentConnected = false) => (
    <div className={styles.treeLevel}>
      {nodes.map(node => (
        <NodeCard
          key={node.id}
          text={node.text}
          hasConnection={parentConnected}
          onAdd={() => handleAddNode(node.id)}
          childrenNodes={renderNodes(node.children, true)}
        />
      ))}
    </div>
  );

  return (
    <div className={styles.container}>
      <div className={styles.headerSection}>
        <div className={styles.headerTitle}>Hierarchy tree</div>
        <div className={styles.groupCountSection}>
          <span className={styles.groupCountLabel}>Groups created :</span>
          <span className={styles.groupCountValue}>{nodes.length}</span>
        </div>
      </div>
      <div className={styles.divider} />
      <div className={styles.treeBody}>
        {renderNodes(nodes)}
      </div>
    </div>
  );
}
