import React, { useState } from "react";
import {
  Typography,
  Button,
  TextField,
  IconButton,
  Box,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import styles from "./CustomNode.module.css";

export const CustomNode = (props) => {
  const { id, text, droppable } = props.node;
  const {
    depth,
    isOpen,
    onToggle,
    isAdding,
    onStartAdd,
    onAdd,
    onCancelAdd,
    isLastChild,
  } = props;
  const [newText, setNewText] = useState("");

  const indent = depth * 24;

  const handleToggle = (e) => {
    e.stopPropagation();
    onToggle(id);
  };

  const handleAddClick = (e) => {
    e.stopPropagation();
    onStartAdd(id);
  };

  const handleSave = () => {
    if (newText) {
      onAdd(id, newText);
      setNewText("");
    }
  };

  const handleCancel = () => {
    onCancelAdd();
    setNewText("");
  };

  const handleInputChange = (e) => {
    setNewText(e.target.value);
  };

  const handleInputKeyPress = (e) => {
    if (e.key === "Enter") handleSave();
    else if (e.key === "Escape") handleCancel();
  };

  return (
    <Box
      className={`${styles.container} ${isLastChild ? styles.isLastChild : ""}`}
      style={{ paddingLeft: indent }}
    >
      <Box className={styles.nodeWrapper}>
        <Box className={styles.connectorDot} />
        <Box className={styles.nodeContent}>
          <Box className={styles.nodeBox}>
            <IconButton
              size="small"
              onClick={handleToggle}
              className={styles.toggleButton}
              aria-label={isOpen ? "Collapse" : "Expand"}
            >
              {droppable ? (
                isOpen ? (
                  <ExpandMoreIcon />
                ) : (
                  <ChevronRightIcon />
                )
              ) : (
                <span style={{ width: 20 }} />
              )}
            </IconButton>
            <Typography variant="body2" className={styles.nodeText}>
              {text}
            </Typography>
          </Box>
          <div className={styles.addButtonWrapper}>
            <IconButton
              size="small"
              onClick={handleAddClick}
              aria-label={`Add child to ${text}`}
            >
              <AddIcon fontSize="inherit" />
            </IconButton>
          </div>
        </Box>
      </Box>

      {isAdding && (
        <Box className={styles.addNodeForm} style={{ paddingLeft: 40 }}>
          <TextField
            autoFocus
            fullWidth
            variant="outlined"
            size="small"
            placeholder="Enter node name..."
            value={newText}
            onChange={handleInputChange}
            onKeyDown={handleInputKeyPress}
            InputProps={{
              endAdornment: (
                <>
                  <Button onClick={handleSave} size="small">
                    Save
                  </Button>
                  <Button onClick={handleCancel} size="small">
                    Cancel
                  </Button>
                </>
              ),
            }}
          />
        </Box>
      )}
    </Box>
  );
};
